package org.mobicents.slee.enabler.ims.userprofile;

import java.io.IOException;

import net.java.slee.resource.diameter.sh.events.avp.userdata.ShData;

public interface IMSUserProfileChild {

  public void setParentSbb(IMSUserProfileParentSbbLocalObject parentSbb);

  // Sh-Pull Operations -------------------------------------------------------

  public String getRepositoryData(String publicIdentity, byte[][] serviceIndication) 
            throws IOException;

  public String getIMSPublicIdentity(String publicIdentity, byte[] msisdn, int identitySet) 
            throws IOException;

  public String getIMSUserState(String publicIdentity) throws IOException;

  public String getSCSCFName(String publicIdentity) throws IOException;

  public String getInitialFilterCriteria(String publicIdentity, String serverName) 
            throws IOException;

  public String getLocationInformation(byte[] msisdn, int requestedDomain) throws IOException;

  public String getUserState(byte[] msisdn, int requestedDomain) throws IOException;

  public String getChargingInformation(String publicIdentity, byte[] msisdn) throws IOException;

  public String getMSISDN(String publicIdentity, byte[] msisdn) throws IOException;

  public String getPSIActivation(String publicIdentity) throws IOException;

  // Sh-Update Operations -----------------------------------------------------

  public String updateRepositoryData(String publicIdentity, ShData data) throws IOException;

  public String updatePSIActivation(String publicIdentity, ShData data) throws IOException;

  // Sh-Subscribe Operations --------------------------------------------------

  public String subscribeRepositoryData(String publicIdentity, byte[][] serviceIndication, 
            int subscriptionRequestType) throws IOException;

  public String subscribeIMSUserState(String publicIdentity, int subscriptionRequestType) 
            throws IOException;

  public String subscribeSCSCFName(String publicIdentity, int subscriptionRequestType) 
            throws IOException;

  public String subscribeInitialFilterCriteria(String publicIdentity, String serverName, 
            int subscriptionRequestType) throws IOException;

  public String subscribePSIActivation(String publicIdentity, int subscriptionRequestType) 
            throws IOException;

}

public IMSUserProfileChildSbbLocalObject getIMSUserProfileChildSbb() {
  final ChildRelation childRelation = getIMSUserProfileChildRelation();
  if (childRelation.isEmpty()) {
    try {
      // creates new instance
      IMSUserProfileChildSbbLocalObject sbb = (IMSUserProfileChildSbbLocalObject) childRelation.create();
      // passes the parent sbb local object to the child
      sbb.setParentSbb((IMSUserProfileParentSbbLocalObject) sbbContext.getSbbLocalObject());
      return sbb;
    }
    catch (Exception e) {
      tracer.severe("Failed to create child sbb", e);
      return null;
    }
  }
  else {
    // reuse the existent one
    return (IMSUserProfileChildSbbLocalObject) childRelation.iterator().next();
  }
}
