package org.mobicents.slee.resource.jdbc.event;

import java.sql.Statement;

/**
 * 
 * @author martins
 * 
 */
public class StatementUpdateCountEventImpl extends AbstractStatementEvent
		implements StatementUpdateCountEvent {

	/**
	 * 
	 */
	private final int updateCount;

	/**
	 * 
	 * @param autoGeneratedKeys
	 * @param columnIndexes
	 * @param columnNames
	 * @param sql
	 * @param statement
	 * @param updateCount
	 */
	public StatementUpdateCountEventImpl(Integer autoGeneratedKeys,
			int[] columnIndexes, String[] columnNames, String sql,
			Statement statement, int updateCount) {
		super(autoGeneratedKeys, columnIndexes, columnNames, sql, statement);
		this.updateCount = updateCount;
	}

	@Override
	public int getUpdateCount() {
		return updateCount;
	}

}
